mapper.select:
SELECT * FROM travel_destination


        (name LIKE CONCAT('%', #{searchQuery}, '%')
        OR cities LIKE CONCAT('%', #{searchQuery}, '%'))


        AND category = #{category}


LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM travel_destination
(name LIKE CONCAT('%', #{searchQuery}, '%') OR cities LIKE CONCAT('%', #{searchQuery}, '%'))
AND category = #{category}

SELECT COUNT(1)
FROM travel_destination
WHERE id = #{id}
