mapper.insert:
INSERT INTO sys_logininfor (
    user_id, username, ipaddr, login_location, browser, os, status, msg, login_time
) VALUES (
    #{userId}, #{username}, #{ipaddr}, #{loginLocation}, #{browser}, #{os}, #{status}, #{msg}, #{loginTime}
)

mapper.select:
SELECT FROM sys_logininfor
AND (username LIKE CONCAT('%', #{keyword}, '%') OR ipaddr LIKE CONCAT('%', #{keyword}, '%') OR login_location LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY login_time DESC LIMIT #{offset}, #{pageSize}
