mapper.select:
SELECT

FROM travel_payments


        AND status = #{status}


        AND (
            order_id LIKE #{keyword}
            OR item_name LIKE #{keyword}
            OR item_id LIKE #{keyword}
        )

    AND username = #{username}

ORDER BY created_at DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*)
FROM travel_payments


        AND status = #{status}


        AND (
        order_id LIKE #{keyword}
        OR item_name LIKE #{keyword}
        OR item_id LIKE #{keyword}
        )

    AND username = #{username}

SELECT FROM travel_payments
AND (sku LIKE CONCAT('%', #{keyword}, '%') OR id = #{keyword})
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM payments


        AND status = #{status}


        AND (
            order_id LIKE #{keyword}
            OR item_name LIKE #{keyword}
            OR item_id LIKE #{keyword}
        )

SELECT * FROM payments
AND status = #{condition.status}
AND (item_name LIKE CONCAT('%', #{condition.keyword}, '%') OR order_id LIKE CONCAT('%', #{condition.keyword}, '%'))
