mapper.select:
SELECT * FROM travel_recommend


        AND (sku LIKE CONCAT('%', #{keyword}, '%') OR id = #{keyword})


LIMIT #{offset}, #{pageSize}

mapper.update:
UPDATE travel_recommend
SET image = #{image},
    name = #{name},
    description = #{description},
    details = #{details}
WHERE id = #{id}

mapper.select:
SELECT COUNT(1)
FROM travel_recommend
WHERE id = #{id}
